import { useState } from "react";

const giftList = ["CPU i9", "RAM 32GB RGB", "RGB Keyboard"];

function App() {
    const [gift, setGift] = useState() 
    const [name, setName] = useState('')
    console.log(name)
    const randomGift = () => {
        const index = Math.floor(Math.random() *(giftList.length))
        setGift(giftList[index])
        console.log(index)
    }

    return(
    <div>
        <div style={ {padding: 32} }>
            <h1>{gift || 'Chua co phan thuong'} </h1>
            <button onClick = {randomGift}>Lay thuong</button>
        </div>
        <div style={ {padding: 32} }>
            <input onChange={setinput => setName(setinput.target.value)}/>
            <button onClick = {randomGift}>Change Name</button>
        </div>
    </div> 
    )
}

export default App;
