import { useState } from "react";

const giftList = ["CPU i9", "RAM 32GB RGB", "RGB Keyboard"];

function App() {
    const [gift, setGift] = useState() 
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')

    const randomGift = () => {
        const index = Math.floor(Math.random() *(giftList.length))
        setGift(giftList[index])
        console.log(index)
    }
    const handlleSubmit = () => {
        console.log({
            name,
            email,
        })
    }
    return(
    <div>
        <div style={ {padding: 32} }>
            <h1>{gift || 'Chua co phan thuong'} </h1>
            <button onClick = {randomGift}>Lay thuong</button>
        </div>
        <div>
            <div style={ {padding: 32} }>
                <input 
                    value={name}
                    onChange={setinput => setName(setinput.target.value)}
                />
                <input 
                    value={email}
                    onChange={setinput => setEmail(setinput.target.value)}
                />
                <button onClick = {() =>{ setName(name); setEmail(email) }}>Change Name</button>
            </div>
        </div>
    </div> 
    )
}

export default App;
