import { useState } from "react";

const giftList = ["CPU i9", "RAM 32GB RGB", "RGB Keyboard"];

const courseList = [
    {
      id: 1,
      name: "HTML, CSS",
    },
    {
      id: 2,
      name: "Javascript",
    },
    {
      id: 3,
      name: "ReactJs",
    },
  ];



function App() {
    const [gift, setGift] = useState() 
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')

    const randomGift = () => {
        const index = Math.floor(Math.random() *(giftList.length))
        setGift(giftList[index])
        console.log(index)
    }
    const handleSubmit = () => {
        console.log({
            name,
            email,
        })
    }
    return(
    <div>
        <div style={ {padding: 32} }>
            <h1>{gift || 'Chua co phan thuong'} </h1>
            <button onClick = {randomGift}>Lấy thưởng</button>
        </div>
        <div>
            <div style={ {padding: 32} }>
                <input 
                    value={name}
                    onChange={setinput => setName(setinput.target.value)}
                />
                <input 
                    value={email}
                    onChange={setinput => setEmail(setinput.target.value)}
                />
                <button onClick = {handleSubmit}>Login</button>
            </div>
        </div>
        <div style={ {padding: 32} } >
            {courseList.map( courseList => (
                <div key = {courseList.id}>
                    <input type= "radio" />
                    {courseList.name}
                </div>
            ))}
            <button onClick = {handleSubmit}>Submit</button>
        </div>
    </div> 
    )
}

export default App;
